import React, { Component } from "react";
import {
  Grid,
  Row,
  Col,
  Table
} from "react-bootstrap";

import { Card } from "../components/Card/Card.jsx";
import CustomButton from "../components/CustomButton/CustomButton.jsx"
import { employeeThArray, employeeTdArray } from "../variables/Variables.jsx";

class Employees extends Component {

  state = {
    employees: []
  }


  async componentDidMount() {

    try{
    const res = await fetch('http://127.0.0.1:8000/api/', {mode:'no-cors'}); // site that doesn’t send Access-Control-*
    const employees = await res.json();
    this.setState({
        employees
    });
  }catch(e){
    console.log(e);
   }
  }
  render() {
    return (
      <div className="content">
        <Grid fluid>
        <Row>
            <Col md={12}>
              <Card
                content={
                  <div><CustomButton block pullRight>Add</CustomButton></div>
                }
              />

              <Card
                title="Employee Overview"
                category=""
                ctTableFullWidth
                ctTableResponsive
                content={
                  <Table striped hover>
                    <thead>
                      <tr>
                        {employeeThArray.map((prop, key) => {
                          return <th key={key}>{prop}</th>;
                        })}
                      </tr>
                    </thead>
                    <tbody>
                      {employeeTdArray.map((prop, key) => {
                        return (
                          <tr key={key}>
                            {prop.map((prop, key) => {
                              return <td key={key}>{prop}</td>;
                            })}
                          </tr>
                        );
                      })}
                    </tbody>
                  </Table>
                }
              />
            </Col>
          </Row>
        </Grid>
      </div>
    );
  }
}

export default Employees;
